import { useEffect, useState } from 'react';
import './App.css';
import SingleCard from './components/SingleCard';


const cardImages = [
  {"src":"/images/bluefire.png", matched: false},
  {"src":"/images/ghost.png", matched: false},
  {"src":"/images/guitar.png", matched: false},
  {"src":"/images/home.png", matched: false},
  {"src":"/images/letter.png", matched: false},
  {"src":"/images/rockstar.png", matched: false}
]

function App() {

  const [cards, setCards] = useState([])
  const [turns, setTurns] = useState(0)
  const [choiceOne, setChoiceOne] = useState(null)
  const [choiceTwo, setChoiceTwo] = useState(null)

  // shuffle card

  const shuffleCards = ()=>{
    const shuffleCards = [...cardImages, ...cardImages]
    .sort(()=> Math.random() - 0.5)
    .map((card)=>({...card,id: Math.random() }))

    setCards(shuffleCards)
    setTurns(0)
  }

 // handle choice

 const handleChoice = (card) =>{
     choiceOne ? setChoiceTwo(card) : setChoiceOne(card);
 }

 // compare two selected cards
 useEffect(()=>{
   if(choiceOne && choiceTwo)
   {
    if(choiceOne.src === choiceTwo.src)
    {
      setCards(prevCard =>{
        return prevCard.map(card =>{
          if(card.src === choiceOne.src)
          {
            return {...card, matched: true}
          }else{
            return card
          }
        })
      })
      resetTurn()
    }else{
      console.log("those cards don't match")
      resetTurn()
    }
   }
 },[choiceOne,choiceTwo])

 console.log(cards);

 // reset choice & increase turn

 const resetTurn = () =>{
  setChoiceOne(null);
  setChoiceTwo(null);
  setTurns(prevTurns => prevTurns + 1)
 }

  return (
    <div className="App">
     <h1>Magic Match</h1>
     <button onClick={shuffleCards}>New Game</button>
     <div className='card-grid'>
       {cards.map(card => (
        <SingleCard 
        key={card.id} 
        card={card}
        handleChoice={handleChoice}
        flipped = {card === choiceOne || card === ch}
        />
       ))}
     </div>
    </div>
  );
}

export default App;
